mapper.select:
SELECT product_id, option_group, option_id, name
  FROM product_option
 WHERE product_id = #{productId}

   AND option_group = #{optionGroup}    


    AND option_id = #{optionId}    

 ORDER BY product_id, option_group, option_id

mapper.delete:
DELETE
  FROM product_option
 WHERE product_id = #{productId}

   AND option_group = #{optionGroup}    


   AND option_id = #{optionId}

mapper.update:
UPDATE product_option
   SET option_id = #{optionId},
       name = #{name}
 WHERE product_id = #{pOption.productId}
   AND option_group = #{pOption.optionGroup}
   AND option_id = #{pOption.optionId}

UPDATE product_option
   SET option_id = LPAD((option_id::INTEGER+1)::CHAR, 2, '0')
 WHERE product_id = #{pOption.productId}
   AND option_group = #{pOption.optionGroup}
   AND option_id  >=  #{startId}
   AND option_id  <=  #{endId}

UPDATE product_option
   SET option_id = LPAD((option_id::INTEGER-1)::CHAR, 2, '0')
 WHERE product_id = #{pOption.productId}
   AND option_group = #{pOption.optionGroup}
   AND option_id  >=  #{startId}
   AND option_id  <=  #{endId}
