mapper.select:
SELECT user_nm, pwd, enabled
  FROM user_main
 WHERE user_nm = #{userNm}

mapper.update:
UPDATE user_main
   SET pwd = #{pwd}
 WHERE user_nm = #{userNm}

UPDATE user_main
   SET enabled = #{enabled}
 WHERE user_nm = #{userNm}

mapper.select:
SELECT authority
  FROM user_auth
 WHERE user_nm = #{userNm}
