mapper.select:
SELECT order_id, delivery_id, seller_nm, type_nm, delivery_nm, price
  FROM ord_dlvr
 WHERE order_id = #{orderId}
   AND delivery_id = #{deliveryId}

mapper.delete:
DELETE
  FROM ord_dlvr
 WHERE order_id = #{orderId}
   AND delivery_id = #{deliveryId}

