mapper.select:
SELECT order_id, delivery_id, seller_nm, type_nm, delivery_nm,
       delivery_price, price, status_cd, ship_cd
  FROM ord_dlvr
 WHERE order_id = #{orderId}
   AND delivery_id = #{deliveryId}

mapper.update:
UPDATE ord_dlvr
   SET delivery_id = #{deliveryId}

        , price = #{price}


        , status_cd = #{statusCd}


        , ship_cd = #{shipCd}

 WHERE order_id = #{orderId}
   AND delivery_id = #{deliveryId}

mapper.delete:
DELETE
  FROM ord_dlvr
 WHERE order_id = #{orderId}
   AND delivery_id = #{deliveryId}
