mapper.select:
SELECT order_id, product_id, option_cd, seller_nm, cnt,
       price, discount_price, product_nm, option_nm,
       status_cd, delivery_id, is_valid
  FROM ord_prod
 WHERE order_id = #{orderId}

    AND product_id = #{productId}

        AND option_cd LIKE #{optionCd}

mapper.delete:
DELETE
  FROM ord_prod
 WHERE order_id = #{orderId}

    AND product_id = #{productId}

        AND option_cd LIKE #{optionCd}

mapper.update:
UPDATE ord_prod
   SET cnt = #{cnt}
 WHERE order_id = #{orderId}
   AND product_id = #{productId}
   AND option_cd = #{optionCd}

UPDATE ord_prod
   SET option_cd = null

           , product_id = null

 WHERE product_id = #{productId}

    AND option_cd like #{optionCd}

mapper.select:
SELECT count(*)
  FROM ord_prod
 WHERE order_id = #{orderId}
   AND delivery_id = #{deliveryId}
   AND is_valid = true

mapper.update:
UPDATE ord_prod
   SET status_cd = #{statusCd},
       is_valid = #{isValid}
WHERE order_id = #{orderId}

    AND product_id = #{productId}

        AND option_cd LIKE #{optionCd}
