mapper.select:
SELECT id, user_nm, order_id, product_id, option_cd, rate, content,
       is_visible, update_dt
  FROM rev_main
 WHERE 1 = 1

        AND product_id = #{productId}

            AND option_cd LIKE #{optionCd}



        AND user_nm = #{userNm}

SELECT count(*)
  FROM rev_main
 WHERE id = #{id}

mapper.update:
UPDATE rev_main
   SET update_dt = now()

        , rate = #{rate}


        , content = #{content}


        , is_visible = #{isVisible}

 WHERE id = #{id}
